mapper.select:
select * from t_ops_userinfo

    
        and userId = #{userId}
    
    
        and userCode = #{userCode}
    
    
        and name = #{name}
    
    
        and email = #{email}
    
    
        and password = #{password}
    
    
        and status = #{status}
    
    
        and deptId = #{deptId}
    

order by createtime desc

select count(*) count from t_ops_userinfo

    
        and userId = #{userId}
    
    
        and userCode = #{userCode}
    
    
        and name = #{name}
    
    
        and email = #{email}
    
    
        and password = #{password}
    
    
        and status = #{status}
    
    
        and deptId = #{deptId}

select * from t_ops_userinfo

    
        and deptId = #{deptId}
    
    
        and status = #{status}
    

order by createtime desc
